import React, { useState, useRef, useEffect } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';

function WebcamDetector({ onDetection }) {
  const [isDetecting, setIsDetecting] = useState(false);
  const [fps, setFps] = useState(5); // Frames por segundo
  const [detectionResults, setDetectionResults] = useState(null);
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  const detectionInterval = useRef(null);

  // Función para dibujar los resultados en el canvas
  const drawResults = (results, imgElement) => {
    const canvas = canvasRef.current;
    if (!canvas || !imgElement) return;
    
    const ctx = canvas.getContext('2d');
    
    // Limpiar canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    
    // Dibujar la imagen
    ctx.drawImage(imgElement, 0, 0, canvas.width, canvas.height);
    
    // Dibujar las cajas de detección
    results.forEach(obj => {
      const [x1, y1, x2, y2] = obj.box;
      
      // Dibujar rectángulo
      ctx.strokeStyle = '#00FF00';
      ctx.lineWidth = 2;
      ctx.strokeRect(x1, y1, x2 - x1, y2 - y1);
      
      // Dibujar etiqueta
      ctx.fillStyle = '#00FF00';
      ctx.font = '16px Arial';
      const labelText = `${obj.label} (${(obj.confidence * 100).toFixed(0)}%)`;
      const textWidth = ctx.measureText(labelText).width;
      
      // Fondo para el texto
      ctx.fillStyle = 'rgba(0, 255, 0, 0.5)';
      ctx.fillRect(x1, y1 > 20 ? y1 - 20 : y1, textWidth + 10, 20);
      
      // Texto
      ctx.fillStyle = '#000000';
      ctx.fillText(labelText, x1 + 5, y1 > 20 ? y1 - 5 : y1 + 15);
    });
  };

  // Función para procesar un frame
  const processFrame = async () => {
    if (!webcamRef.current || !canvasRef.current) return;
    
    // Obtener screenshot de la webcam
    const imageSrc = webcamRef.current.getScreenshot();
    if (!imageSrc) return;

    try {
      // Convertir data URL a Blob
      const blob = await fetch(imageSrc).then(res => res.blob());
      const formData = new FormData();
      formData.append('file', blob, 'webcam.jpg');

      // Enviar al backend para detección
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      // Crear elemento de imagen para dibujar
      const img = new Image();
      img.onload = () => {
        // Ajustar canvas al tamaño del video
        const video = webcamRef.current.video;
        canvasRef.current.width = video.videoWidth;
        canvasRef.current.height = video.videoHeight;
        
        // Dibujar resultados
        drawResults(response.data.results, img);
      };
      img.src = `http://localhost:5000${response.data.processed_image}`;
      
      // Actualizar resultados
      setDetectionResults({
        results: response.data.results,
        imageUrl: `http://localhost:5000${response.data.processed_image}`
      });
      
      if (onDetection) {
        onDetection({
          results: response.data.results,
          imageUrl: `http://localhost:5000${response.data.processed_image}`
        });
      }
    } catch (error) {
      console.error('Detection error:', error);
    }
  };

  // Iniciar/detener la detección
  const toggleDetection = () => {
    if (isDetecting) {
      clearInterval(detectionInterval.current);
      detectionInterval.current = null;
      setDetectionResults(null);
    } else {
      // Procesar inmediatamente y luego a intervalos regulares
      processFrame();
      detectionInterval.current = setInterval(processFrame, 1000 / fps);
    }
    setIsDetecting(!isDetecting);
  };

  // Efecto para limpieza al desmontar
  useEffect(() => {
    return () => {
      if (detectionInterval.current) {
        clearInterval(detectionInterval.current);
      }
    };
  }, []);

  return (
    <div className="webcam-detector">
      <h2>Real-time Detection</h2>
      <div style={{ position: 'relative' }}>
        <Webcam
          ref={webcamRef}
          audio={false}
          screenshotFormat="image/jpeg"
          style={{ display: 'block' }}
          videoConstraints={{
            facingMode: 'environment',
            width: 1280,
            height: 720
          }}
        />
        <canvas
          ref={canvasRef}
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            pointerEvents: 'none'
          }}
        />
      </div>
      <div className="controls">
        <button onClick={toggleDetection}>
          {isDetecting ? 'Stop Detection' : 'Start Detection'}
        </button>
        <div className="fps-control">
          <label>Detection FPS: </label>
          <input 
            type="range" 
            min="1" 
            max="10" 
            value={fps} 
            onChange={(e) => setFps(parseInt(e.target.value))}
            disabled={isDetecting}
          />
          <span> {fps}</span>
        </div>
      </div>
      
      {detectionResults && (
        <div className="detection-results">
          <h3>Last Detection Results</h3>
          <ul>
            {detectionResults.results.map((obj, index) => (
              <li key={index}>
                <strong>{obj.label}</strong> - Confidence: {(obj.confidence * 100).toFixed(2)}%
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default WebcamDetector;
