import React, { useState, useEffect, useRef } from 'react';
import socketio from 'socket.io-client';

function WebcamDetector({ onDetection }) {
  const [isDetecting, setIsDetecting] = useState(false);
  const [fps, setFps] = useState(10);  // FPS por defecto aumentados
  const videoRef = useRef(null);
  const socketRef = useRef(null);
  const frameQueue = useRef([]);
  const lastFrameTime = useRef(0);
  const animationFrameId = useRef(null);

  // Procesamiento de frames en el frontend para mayor fluidez
  const processFrameQueue = () => {
    const now = performance.now();
    const minFrameInterval = 1000 / fps;
    
    if (frameQueue.current.length > 0 && now - lastFrameTime.current >= minFrameInterval) {
      const frameData = frameQueue.current.shift();
      if (videoRef.current) {
        videoRef.current.src = `data:image/jpeg;base64,${frameData.frame}`;
      }
      if (onDetection) {
        onDetection({
          type: 'video',
          frame: `data:image/jpeg;base64,${frameData.frame}`,
          results: frameData.results,
          timestamp: frameData.timestamp
        });
      }
      lastFrameTime.current = now;
    }
    
    animationFrameId.current = requestAnimationFrame(processFrameQueue);
  };

  useEffect(() => {
    // Iniciar el procesamiento de frames
    animationFrameId.current = requestAnimationFrame(processFrameQueue);
    
    return () => {
      if (animationFrameId.current) {
        cancelAnimationFrame(animationFrameId.current);
      }
    };
  }, [fps]);

  useEffect(() => {
    // Configurar Socket.IO
    socketRef.current = socketio('http://localhost:5000', {
      transports: ['websocket'],
      upgrade: false
    });
    
    socketRef.current.on('video_frame', (data) => {
      // Limitar el tamaño de la cola para evitar retrasos
      if (frameQueue.current.length < 3) {
        frameQueue.current.push(data);
      }
    });

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, []);

  const toggleDetection = () => {
    if (isDetecting) {
      socketRef.current.emit('stop_video');
    } else {
      // Limpiar cola de frames al iniciar
      frameQueue.current = [];
      socketRef.current.emit('start_video', { fps });
    }
    setIsDetecting(!isDetecting);
  };

  return (
    <div className="webcam-detector">
      <h2>Real-time Object Detection</h2>
      
      <div className="video-container">
        <img 
          ref={videoRef}
          alt="Live camera feed"
          style={{ 
            maxWidth: '100%',
            border: '2px solid #333',
            borderRadius: '4px'
          }}
        />
      </div>
      
      <div className="controls">
        <button onClick={toggleDetection} className="detection-button">
          {isDetecting ? (
            <>
              <span className="pulse-icon">●</span> Stop Detection
            </>
          ) : (
            'Start Detection'
          )}
        </button>
        
        <div className="fps-control">
          <label>Detection FPS: </label>
          <input 
            type="range" 
            min="1" 
            max="15" 
            value={fps} 
            onChange={(e) => setFps(parseInt(e.target.value))}
            disabled={isDetecting}
          />
          <span className="fps-value">{fps}</span>
        </div>
      </div>
    </div>
  );
}

export default WebcamDetector;
