import React, { useState, useEffect, useRef } from 'react';
import socketio from 'socket.io-client';

function WebcamDetector({ onDetection }) {
  const [isDetecting, setIsDetecting] = useState(false);
  const [fps, setFps] = useState(5);
  const videoRef = useRef(null);
  const socketRef = useRef(null);

  useEffect(() => {
    // Conectar al servidor Socket.IO
    socketRef.current = socketio('http://localhost:5000');
    
    // Manejar frames de video
    socketRef.current.on('video_frame', (data) => {
      onDetection({
        type: 'video',
        frame: `data:image/jpeg;base64,${data.frame}`,
        results: data.results
      });
    });

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
    };
  }, []);

  const toggleDetection = () => {
    if (isDetecting) {
      socketRef.current.emit('stop_video');
    } else {
      socketRef.current.emit('start_video', { fps });
    }
    setIsDetecting(!isDetecting);
  };

  return (
    <div className="webcam-detector">
      <h2>Real-time Detection</h2>
      <div className="controls">
        <button onClick={toggleDetection}>
          {isDetecting ? 'Stop Detection' : 'Start Detection'}
        </button>
        <div className="fps-control">
          <label>FPS: </label>
          <input 
            type="range" 
            min="1" 
            max="10" 
            value={fps} 
            onChange={(e) => setFps(parseInt(e.target.value))}
            disabled={isDetecting}
          />
          <span> {fps}</span>
        </div>
      </div>
    </div>
  );
}

export default WebcamDetector;
