import React, { useState } from 'react';  // Importar React y useState hook
import ImageUploader from './components/ImageUploader';  // Componente para subir imágenes
import WebcamDetector from './components/WebcamDetector';  // Componente de cámara
import ResultsDisplay from './components/ResultsDisplay';  // Componente de resultados
import './App.css';  // Estilos CSS

function App() {
  // Estado para almacenar la última detección
  const [detection, setDetection] = useState(null);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Object Detection App</h1>
        <p>All processing happens in Python backend</p>
      </header>
      <main>
        {/* Contenedor de métodos de detección */}
        <div className="detection-methods">
          <ImageUploader onDetection={setDetection} />
          <WebcamDetector onDetection={setDetection} />
        </div>
        {/* Mostrar resultados */}
        <ResultsDisplay detection={detection} />
      </main>
    </div>
  );
}

export default App;  // Exportar componente principal
